/**
 * @return {ReactElement} Displays profile page
 */
export default function Availability({uid}) {
    const usersCollection = useFirestore().collection('users');
    const userData = useFirestoreDocData(
        usersCollection.doc(uid)
    );
    const [schedule, setSchedule] = React.useState(userData.Availability);

    const handleChange = newSchedule => {
        setSchedule(newSchedule)
        console.log(newSchedule);
        //   firebase.firestore.Timestamp(newSchedule[0]);
        let date;
        for (date in newSchedule) {
            usersCollection.doc(uid).update({'availability': firebase.firestore.Timestamp.fromDate(date)});
        }
    }

    return (
    <ScheduleSelector
        selection={schedule}
        numDays={5}
        minTime={8}
        maxTime={22}
        hourlyChunks={2}
        onChange={handleChange}
    />
    )
}
